package main

import (
	"encoding/hex"
	"fmt"
	"os"
	"time"

	crypto "github.com/libp2p/go-libp2p-crypto"
	peer "github.com/libp2p/go-libp2p-peer"
	"github.com/wanyvic/prizes_ssh/network"
)

func main() {
	if len(os.Args) < 2 {
		fmt.Println("need args")
		return
	}
	var priv_bytes []byte
	if os.Args[1] == "1" {
		priv_bytes, _ = hex.DecodeString("")
	} else if os.Args[1] == "2" {
		priv_bytes, _ = hex.DecodeString("")
		// connectID = "QmWUoM722bdZdtLmore85BsaXhvjsgRTwiGYGLH6AwoeHF"
	} else if os.Args[1] == "3" {
		priv_bytes, _ = hex.DecodeString("")
		// connectID = "Qmc3gXTLxPSTbapdcubUKve9EJ5uSrNdnHsHMgNCru1HxY"
	} else if os.Args[1] == "4" {
		priv_bytes, _ = hex.DecodeString("")
		// connectID = "Qmc3gXTLxPSTbapdcubUKve9EJ5uSrNdnHsHMgNCru1HxY"
	}
	priva, err := crypto.UnmarshalPrivateKey(priv_bytes)
	if err != nil {
		panic(err)
	}
	host, err := network.New(priva)
	_ = host
	_ = err

	id1, err := peer.IDB58Decode("QmWUoM722bdZdtLmore85BsaXhvjsgRTwiGYGLH6AwoeHF")
	if err != nil {
		fmt.Println(err)
	}
	for {
		err = host.Connect(id1)
		if err == nil {
			break
		}
		fmt.Println(err)
		time.Sleep(time.Second)
	}

	select {}
}
